import React, { useState } from "react";
import p5 from "p5";

document.addEventListener('DOMContentLoaded', () => {
  const [waveAmplitude, setWaveAmplitude] = useState(50);
  const [waveSaturation, setWaveSaturation] = useState(50);
  const [waveColor, setWaveColor] = useState(127);
  const [waveOpacity, setWaveOpacity] = useState(127);


  const sketch = (p) => {
    p.setup = () => {
      const canvas = p.createCanvas(700, 410)
        canvas.parent('prototype_16')
      p.frameRate(1);
    };

    p.draw = () => {
      p.background(0);
      let waveColorValue = p.color(waveSaturation, waveColor, 0, waveOpacity);
      let waveCount = 10;
      let waveSpacing = p.width / (waveCount - 1);

      for (let i = 0; i < waveCount; i++) {
        let x = i * waveSpacing;
        let y = p.height / 2 + p.sin(p.frameCount * 0.05 + i * 0.5) * waveAmplitude;
        p.fill(waveColorValue);
        p.noStroke();
        p.ellipse(x, y, p.random(20, 50), p.random(60, 150));
      }
      p.filter(p.BLUR, p.random(40, 80));
    };
  };

  new p5(sketch);

  return (
    <div>
      <div ref={canvasRef}></div>
      <div>
        <label htmlFor="waveAmplitudeSlider">Wave Amplitude:</label>
        <input
          type="range"
          min="0"
          max="100"
          value={waveAmplitude}
          onChange={(e) => setWaveAmplitude(e.target.value)}
          id="waveAmplitudeSlider"
        />
      </div>
      <div>
        <label htmlFor="waveSaturationSlider">Wave Saturation:</label>
        <input
          type="range"
          min="0"
          max="100"
          value={waveSaturation}
          onChange={(e) => setWaveSaturation(e.target.value)}
          id="waveSaturationSlider"
        />
      </div>
      <div>
        <label htmlFor="waveColorSlider">Wave Color:</label>
        <input
          type="range"
          min="0"
          max="255"
          value={waveColor}
          onChange={(e) => setWaveColor(e.target.value)}
          id="waveColorSlider"
        />
      </div>
      <div>
        <label htmlFor="waveOpacitySlider">Wave Opacity:</label>
        <input
          type="range"
          min="0"
          max="255"
          value={waveOpacity}
          onChange={(e) => setWaveOpacity(e.target.value)}
          id="waveOpacitySlider"
        />
      </div>
    </div>
  );
});

export default Sketch;
