import React, { useState, useEffect } from "react";
import p5 from "p5";

function Prototype16() {

  const [waveAmplitude, setWaveAmplitude] = useState(50);
  const [waveSaturation, setWaveSaturation] = useState(50);
  const [waveColor, setWaveColor] = useState([127, 0, 0, 127]);
  const [waveOpacity, setWaveOpacity] = useState(127);

  const handleWaveAmplitudeChange = (event) => {
    setWaveAmplitude(event.target.value);
  };

  const handleWaveSaturationChange = (event) => {
    setWaveSaturation(event.target.value);
  };

  const handleWaveColorChange = (event) => {
    setWaveColor([
      waveSaturation,
      event.target.value,
      waveColor[2],
      waveOpacity,
    ]);
  };

  const handleWaveOpacityChange = (event) => {
    setWaveOpacity(event.target.value);
  };

  useEffect(() => {
    const sketch = (p) => {
      p.setup = () => {
        const canvas = p.createCanvas(700, 410);
        canvas.parent('prototype_16');
        p.frameRate(1);
      };

      p.draw = () => {
        p.background(0);
        let waveCount = 10;
        let waveSpacing = p.width / (waveCount - 1);

        for (let i = 0; i < waveCount; i++) {
          let x = i * waveSpacing;
          let y = p.height / 2 + p.sin(p.frameCount * 0.05 + i * 0.5) * waveAmplitude;
          p.fill(p.color(waveSaturation, waveColor[1], waveColor[2], waveOpacity));
          p.noStroke();
          p.ellipse(x, y, p.random(20, 50), p.random(60, 150));
        }
        p.filter(p.BLUR, p.random(40, 80));
      };
    };

    new p5(sketch);
  }, [waveAmplitude, waveSaturation, waveColor, waveOpacity]);



  return (
    <div>
      <div>
        <label htmlFor="waveAmplitudeSlider">Wave Amplitude:</label>
        <input
          type="range"
          min="0"
          max="100"
          value={waveAmplitude}
          onChange={handleWaveAmplitudeChange}
          id="waveAmplitudeSlider"
        />
      </div>
      <div>
        <label htmlFor="waveSaturationSlider">Wave Saturation:</label>
        <input
          type="range"
          min="0"
          max="100"
          value={waveSaturation}
          onChange={handleWaveSaturationChange}
          id="waveSaturationSlider"
        />
      </div>
      <div>
        <label htmlFor="waveColorSlider">Wave Color:</label>
        <input
          type="range"
          min="0"
          max="255"
          value={waveColor[1]}
          onChange={handleWaveColorChange}
          id="waveColorSlider"
        />
      </div>
      <div>
        <label htmlFor="waveOpacitySlider">Wave Opacity:</label>
        <input
          type="range"
          min="0"
          max="255"
          value={waveOpacity}
          onChange={handleWaveOpacityChange}
          id="waveOpacitySlider"
        />
      </div>
    </div>
  );
}



export default Prototype16;
