import React, { useRef, useState, useEffect } from "react";
import p5 from "p5";

function Prototype16() {
  const containerRef = useRef();

  const [waveAmplitude, setWaveAmplitude] = useState(50);
  const [waveSaturation, setWaveSaturation] = useState(50);
  const [waveBrightness, setWaveBrightness] = useState(50);
  const [waveOpacity, setWaveOpacity] = useState(127);
  const [waveColor, setWaveColor] = useState([waveSaturation, waveBrightness, waveOpacity]);

  useEffect(() => {
    const sketch = (p) => {
      p.setup = () => {
        p.createCanvas(700, 410);
        p.colorMode(p.HSB, 100, 100, 100, 255);
        p.frameRate(1);
      };

      p.draw = () => {
        p.background(0);
        const waveCount = 10;
        const waveSpacing = p.width / (waveCount - 1);

        for (let i = 0; i < waveCount; i++) {
          const x = i * waveSpacing;
          const y = p.height / 2 + p.sin(p.frameCount * 0.05 + i * 0.5) * waveAmplitude;
          const waveColorHue = waveSaturation;
          const waveColorBrightness = waveBrightness;
          const waveColorAlpha = waveOpacity;
          p.fill(waveColorHue, waveColorBrightness, 100, waveColorAlpha);
          p.noStroke();
          p.ellipse(x, y, p.random(20, 50), p.random(60, 150));
        }
         //p.filter(p.BLUR, p.random(40, 80));
      };
    };

    const p5Instance = new p5(sketch, containerRef.current);

    return () => {
      p5Instance.remove();
    };
  }, [waveAmplitude, waveSaturation, waveBrightness, waveOpacity]);

  const handleWaveAmplitudeChange = (event) => {
    setWaveAmplitude(event.target.value);
  };

  const handleWaveSaturationChange = (event) => {
    setWaveSaturation(event.target.value);
    setWaveColor([event.target.value, waveBrightness, waveOpacity]);
  };

  const handleWaveBrightnessChange = (event) => {
    setWaveBrightness(event.target.value);
    setWaveColor([waveSaturation, event.target.value, waveOpacity]);
  };

  const handleWaveOpacityChange = (event) => {
    setWaveOpacity(event.target.value);
    setWaveColor([waveSaturation, waveBrightness, event.target.value]);
  };

  return (
    <div>
      <div ref={containerRef}></div>
      <div>
        <label htmlFor="waveAmplitudeSlider">Wave Amplitude:</label>
        <input
          type="range"
          min="0"
          max="100"
          value={waveAmplitude}
          onChange={handleWaveAmplitudeChange}
          id="waveAmplitudeSlider"
        />
      </div>
      <div>
        <label htmlFor="waveSaturationSlider">Wave Saturation:</label>
        <input
          type="range"
          min="0"
          max="100"
          value={waveSaturation}
          onChange={handleWaveSaturationChange}
          id="waveSaturationSlider"
        />
      </div>
      <div>
        <label htmlFor="waveBrightnessSlider">Wave Brightness:</label>
        <input
          type="range"
          min="0"
          max="100"
          value={waveBrightness}
          onChange={handleWaveBrightnessChange}
          id="waveBrightnessSlider"
        />
      </div>
      <div>
        <label htmlFor="waveOpacitySlider">Wave Opacity:</label>
        <input
          type="range"
          min="0"
          max="100"
          value={waveOpacity}
          onChange={handleWaveOpacityChange}
          id="waveOpacitySlider"
        />
      </div>
      </div>
  );
 }
 export default Prototype16;
