import { isAudioNode } from '../guards/audio-node';
import { isDelayNode } from '../guards/delay-node';
export const createDetectCycles = (audioParamAudioNodeStore, getAudioNodeConnections, getValueForKey) => {
  return function detectCycles(chain, nextLink) {
    const audioNode = isAudioNode(nextLink) ? nextLink : getValueForKey(audioParamAudioNodeStore, nextLink);

    if (isDelayNode(audioNode)) {
      return [];
    }

    if (chain[0] === audioNode) {
      return [chain];
    }

    if (chain.includes(audioNode)) {
      return [];
    }

    const _getAudioNodeConnecti = getAudioNodeConnections(audioNode),
          outputs = _getAudioNodeConnecti.outputs;

    return Array.from(outputs).map(outputConnection => detectCycles([...chain, audioNode], outputConnection[0])).reduce((mergedCycles, nestedCycles) => mergedCycles.concat(nestedCycles), []);
  };
};
