import * as Tone from 'tone'
import React, { Component } from 'react'

import * as banjoSettings from './tunes/banjo.js'

import ToneSynth from './modules/ToneSynth.jsx'
import Channel from './modules/Channel.jsx'
import SC_Button from './components/SC_Button'
import SC_Slider from './components/SC_Slider'

let banjoChannel
let banjoSynth
//let banjoFreeverb

export default class Container extends Component {
  constructor(props) {
    super(props)

    this.state = {
      banjoSettings
    }
  }

  handleStart = () => {
    const { banjoSettings } = this.state

    banjoSynth = new Tone.Synth(banjoSettings.synth)
    //banjoFreeverb = new Tone.Freeverb(banjoSettings.freeverb).start()
    banjoChannel = new Tone.Channel(banjoSettings.channel).toDestination()

    banjoSynth.chain(banjoChannel)

    const sampler = new Tone.Sampler({
      urls: {
        A1: 'japan-style-synth-banjo-drop_138bpm_B_major.wav',
        A2: 'A#4_ShakuhaciLong_01_SP.wav'
      },
      baseUrl: 'http://localhost:3000/samples/'
    }).toDestination()

    const banjoPart = new Tone.Part((time, note) => {
      sampler.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, banjoSettings.sequence.steps).start(0)

    banjoPart.loopEnd = banjoSettings.sequence.duration
    banjoPart.loop = true

    Tone.Transport.start()
  }

  handleBanjoValueChange = (property, value) => {
    const { banjoSettings } = this.state

    if (property === 'synthType') {
      banjoSynth.oscillator.type = value
      banjoSettings.synth.oscillator.type = value
    } else if (property === 'synthEnvelopeAttack') {
      banjoSynth.envelope.attack = value
      banjoSettings.synth.envelope.attack = value
    } else if (property === 'synthEnvelopeDecay') {
      banjoSynth.envelope.decay = value
      banjoSettings.synth.envelope.decay = value
    } else if (property === 'synthEnvelopeSustain') {
      banjoSynth.envelope.sustain = value
      banjoSettings.synth.envelope.sustain = value
    } else if (property === 'synthEnvelopeRelease') {
      banjoSynth.envelope.release = value
      banjoSettings.synth.envelope.release = value
      // } else if (property === 'freeverbWet') {
      //   banjoFreeverb.wet.value = value
      //   banjoSettings.freeverb.wet = value
      // } else if (property === 'freeverbRoomSize') {
      //   banjoFreeverb.roomSize.value = value
      //   banjoSettings.freeverb.roomSize = value
      // } else if (property === 'freeverbDampening') {
      //   banjoFreeverb.dampening.value = value
      //   banjoSettings.freeverb.dampening = value
    } else if (property === 'channelVolume') {
      banjoChannel.volume.value = value
      banjoSettings.channel.volume = value
    } else if (property === 'channelMute') {
      banjoChannel.mute.value = value
      banjoSettings.channel.mute = value
    }

    this.setState({
      banjoSettings
    })
  }

  render() {
    const { banjoSettings } = this.state

    return (
      <div className="Container">
        <SC_Button text="Start" handleClick={this.handleStart} />

        <Channel
          settings={banjoSettings}
          handleValueChange={this.handleBanjoValueChange}
        />
        <ToneSynth
          settings={banjoSettings}
          handleValueChange={this.handleBanjoValueChange}
        />
      </div>
    )
  }
}
